mapper.select:
select count(1) from CHANNEL_AGENT_DETAIL_HISTORY cad left join CHANNEL_MAIN_HISTORY pb on pb.HISTORY_ID = cad.HISTORY_ID
and DEPT_CODE = #{deptCode,jdbcType=VARCHAR}
and pb.VALID_IND = 1

select * from ( select a.*,rownum rn from ( select pb.HISTORY_ID,pb.DEPT_CODE,dept_simple_name, cad.CHANNEL_CODE,CHANNEL_NAME, cg.CATEGORY_NAME CATEGORY, decode(pb.APPROVE_FLAG, '1', '已审核', '0', '未审核', '已审核') APPROVE_FLAG, pb.updated_user APPROVE_CODE, decode(CHANNEL_FLAG, '0', '代理机构', '1', '个人代理', '') CHANNEL_FLAG, MOBILE,cad.TEL,cad.LICENSE_NO,cad.BUSINESS_NO, decode(cad.SEX, '106001', '男', '106002', '女', '106003', '不清楚', '106009', '未知', '') SEX, et.EDUCATIOIN_NAME EDUCATIOIN,DUTY, tt.TITLE_NAME TITLE, ct.CERTIFY_TYPE_NAME CERTIFY_TYPE, cad.CERTIFY_NO,BANK_NAME,BANK_ACCOUNT, BANK_NODE, decode(pb.STATUS, '1', '有效', '') STATUS, cad.UPDATED_DATE from CHANNEL_MAIN_HISTORY pb left join CHANNEL_AGENT_DETAIL_HISTORY cad on pb.History_Id = cad.History_Id left join (select * from CATEGORY where VALID_IND = 1) cg on pb.CATEGORY = cg.CATEGORY_CODE left join DEPARTMENT dept on pb.DEPT_CODE = dept.DEPT_CODE and dept.VALID_IND = 1 left join (select * from CERTIFY_TYPE where VALID_IND = 1) ct on cad.CERTIFY_TYPE = ct.CERTIFY_TYPE_CODE left join (select * from EDUCATIOIN where VALID_IND = 1) et on cad.EDUCATIOIN = et.EDUCATIOIN_CODE left join (select * from TITLE where VALID_IND = 1) tt on cad.TITLE = tt.TITLE_CODE left join salesman sa on pb.APPROVE_CODE = sa.employ_code
and DEPT_CODE = #{deptCode,jdbcType=VARCHAR}
and pb.VALID_IND = 1 group by pb.HISTORY_ID, pb.DEPT_CODE,dept_simple_name, cad.CHANNEL_CODE,CHANNEL_NAME, cg.CATEGORY_NAME,CHANNEL_FLAG, MOBILE,cad.TEL,cad.LICENSE_NO,cad.BUSINESS_NO, cad.SEX,et.EDUCATIOIN_NAME,DUTY,tt.TITLE_NAME, ct.CERTIFY_TYPE_NAME, cad.CERTIFY_NO,BANK_NAME,BANK_ACCOUNT, BANK_NODE, pb.APPROVE_FLAG, pb.updated_user, pb.STATUS, cad.UPDATED_DATE order by cad.UPDATED_DATE desc ) a where rownum <= #{endpoint} ) where rn >= #{startpoint}

