select count(1)
from UM_DEPARTMENT
where 1=1

	and TAXRGST_NO = #{taxrgstNo,jdbcType=VARCHAR}


	and DPT_REL_CDE = #{dptRelCde,jdbcType=VARCHAR}


	and DPT_CNM = #{dptCnm,jdbcType=VARCHAR}


	and CREATED_USER = #{createdUser,jdbcType=VARCHAR}


	and BNSRGST_NO = #{bnsrgstNo,jdbcType=VARCHAR}


	and UPDATED_DATE = #{updatedDate,jdbcType=DATE}


	and DEPARTMENT_MRK = #{departmentMrk,jdbcType=VARCHAR}


	and VALID_IND = #{validInd,jdbcType=CHAR}


	and DPT_CADDR = #{dptCaddr,jdbcType=VARCHAR}


	and CREATED_DATE = #{createdDate,jdbcType=DATE}


	and CTCT_CDE = #{ctctCde,jdbcType=VARCHAR}


	and DPT_ABR = #{dptAbr,jdbcType=VARCHAR}


	and RPT_ADDR = #{rptAddr,jdbcType=VARCHAR}


	and DPT_CDE = #{dptCde,jdbcType=VARCHAR}


	and TEL = #{tel,jdbcType=VARCHAR}


	and INSPRMT_NO = #{insprmtNo,jdbcType=VARCHAR}


	and RPT_TEL = #{rptTel,jdbcType=VARCHAR}


	and FAX = #{fax,jdbcType=VARCHAR}


	and N_DPT_LEVL <= #{nDptLevl,jdbcType=INTEGER}


	and ZIP_CDE = #{zipCde,jdbcType=VARCHAR}


	and UPDATED_USER = #{updatedUser,jdbcType=VARCHAR}


	and DPT_EADDR = #{dptEaddr,jdbcType=VARCHAR}


	and DPT_SERNO = #{dptSerno,jdbcType=VARCHAR}


	and C_DPT_ENM = #{cDptEnm,jdbcType=VARCHAR}


	and LCN_ABR = #{lcnAbr,jdbcType=VARCHAR}


	and INTER_CDE = #{interCde,jdbcType=VARCHAR}


	and FND_TM = #{fndTm,jdbcType=DATE}


	and C_SNR_DPT = #{cSnrDpt,jdbcType=VARCHAR}


	and ALARM_MRK = #{alarmMrk,jdbcType=CHAR}


	and CONS_TEL = #{consTel,jdbcType=VARCHAR}


	and ABD_TM = #{abdTm,jdbcType=DATE}

