select t.c_dpt_cde as "id", t.c_dpt_abr as "text", t.c_snr_dpt as "pid", t.c_dpt_cde as "departCode" from sale_department t where 1=1 and t.valid_ind = '1'
and t.c_snr_dpt = #{parentCode}
order by t.c_dpt_cde

select DEPARTMENT_ID as "id",DEPARTMENT_ATTR_NAME as "text",SUPER_DEPARTMENT_ID as "pid",DEPARTMENT_CODE as "departCode" from hr_department_information 

