mapper.select:
	select * from (
		select a.*,rownum rn from (
		select 
		,
		 (select depa.dept_simple_name
          from department depa
         where depa.dept_code = de.parent_dept_code) as "parentDept",
       de.dept_simple_name as "deptName"
		from SURVEY s 
		 inner join department de
    on s.dept_code = de.dept_code

			and exists (
		       select 1 from department dep 
		       where de.parent_dept_code = dep.dept_code and dep.dept_code = #{parentDept}
       		)


		) a where rownum <= #{endpoint}
	) where rn >= #{startpoint}

